/* Slider */

.slick-slider {
  position: relative;
  display: block;
  box-sizing: border-box;
  -webkit-touch-callout: none;
  -webkit-user-select: none;
  -khtml-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  -ms-touch-action: pan-y;
  touch-action: pan-y;
  -webkit-tap-highlight-color: transparent;
}

.slick-list {
  position: relative;
  overflow: hidden;
  display: block;
  margin: 0;
  padding: 0;
}

.slick-list:focus {
  outline: none;
}

.slick-list.dragging {
  cursor: pointer;
  cursor: hand;
}

.slick-slider .slick-list,
.slick-slider .slick-track {
  -webkit-transform: translate3d(0, 0, 0);
  -moz-transform: translate3d(0, 0, 0);
  -ms-transform: translate3d(0, 0, 0);
  -o-transform: translate3d(0, 0, 0);
  transform: translate3d(0, 0, 0);
}

.slick-track {
  position: relative;
  left: 0;
  top: 0;
  display: block;
  margin-left: auto;
  margin-right: auto;
}

.slick-track::after,
.slick-track::before {
  content: "";
  display: table;
}

.slick-track::after {
  clear: both;
}

.slick-loading .slick-track {
  visibility: hidden;
}

.slick-slide {
  float: left;
  height: 100%;
  min-height: 1px;
  display: none;
}

[dir="rtl"] .slick-slide {
  float: right;
}

.slick-slide img {
  display: block;
}

.slick-slide.slick-loading img {
  display: none;
}

.slick-slide.dragging img {
  pointer-events: none;
}

.slick-initialized .slick-slide {
  display: block;
}

.slick-loading .slick-slide {
  visibility: hidden;
}

.slick-vertical .slick-slide {
  display: block;
  height: auto;
  border: 1px solid transparent;
}

.slick-arrow.slick-hidden {
  display: none;
}

.slick-slideshow .slick-slide {
  visibility: hidden;
  opacity: 0;
  background-color: #31302e !important;
  z-index: -1;
  transition: all 0.5s cubic-bezier(0.28, 0, 0.18, 1) !important;
}

.slick-slideshow .slick-slide.slick-active {
  z-index: 1;
  visibility: visible;
  opacity: 1 !important;
}

.slick-slideshow.slick-slider .slick-background {
  transition: transform 11.5s cubic-bezier(0.28, 0, 0.18, 1);
  transition-delay: 0.25s;
  transform: scale(1.1, 1.1);
}

.slick-slideshow.slick-slider .slick-active > .slick-background {
  transform: scale(1.001, 1.001) translate3d(0, 0, 0);
  transform-origin: 50% 50%;
}

.slick-arrow {
  display: block;
  width: 3.75rem;
  height: 3.75rem;
  background-color: #31302e;
  position: absolute;
  top: 50%;
  z-index: 99;
  cursor: pointer;
  transform: translateY(-50%);
  transition: all 0.25s ease;
}

.slick-arrow:hover {
  background-color: #24374d;
}

@media (max-width: 500px) {
  .slick-arrow {
    display: none !important;
  }
}

.slick-gallery .slick-dots {
  height: 2.5rem;
  line-height: 2.5rem;
  width: 100%;
  list-style: none;
  text-align: center;
}

.slick-gallery .slick-dots li {
  position: relative;
  display: inline-block;
  margin: 0;
  padding: 0 0.3125rem;
  cursor: pointer;
}

.slick-gallery .slick-dots li button {
  padding: 0;
  border-radius: 3.125rem;
  border: 0;
  display: block;
  height: 0.625rem;
  width: 0.625rem;
  outline: none;
  line-height: 0;
  font-size: 0;
  color: transparent;
  background: #b2adaa;
}

.slick-gallery .slick-dots li::before,
.slick-gallery .slick-dots li button::after {
  display: none !important;
}

.slick-gallery .slick-dots li.slick-active button {
  background-color: #24374d;
}

/* Magnific Popup CSS */

.mfp-bg {
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: 10001;
  overflow: hidden;
  position: fixed;
  background: #0b0b0b;
  opacity: 0.8;
}

.mfp-wrap {
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: 10002;
  position: fixed;
  outline: none !important;
  -webkit-backface-visibility: hidden;
}

.mfp-container {
  text-align: center;
  position: absolute;
  width: 100%;
  height: 100%;
  left: 0;
  top: 0;
  padding: 0 8px;
  box-sizing: border-box;
}

.mfp-container::before {
  content: '';
  display: inline-block;
  height: 100%;
  vertical-align: middle;
}

.mfp-align-top .mfp-container::before {
  display: none;
}

.mfp-content {
  position: relative;
  display: inline-block;
  vertical-align: middle;
  margin: 0 auto;
  text-align: left;
  z-index: 10004;
}

.mfp-inline-holder .mfp-content,
.mfp-ajax-holder .mfp-content {
  width: 100%;
  cursor: auto;
}

.mfp-ajax-cur {
  cursor: progress;
}

.mfp-zoom-out-cur {
  overflow: hidden;
}

.mfp-zoom-out-cur,
.mfp-zoom-out-cur .mfp-image-holder .mfp-close {
  cursor: -moz-zoom-out;
  cursor: -webkit-zoom-out;
  cursor: zoom-out;
}

.mfp-zoom {
  cursor: pointer;
  cursor: -webkit-zoom-in;
  cursor: -moz-zoom-in;
  cursor: zoom-in;
}

.mfp-auto-cursor .mfp-content {
  cursor: auto;
}

.mfp-close,
.mfp-arrow,
.mfp-preloader,
.mfp-counter {
  user-select: none;
}

.mfp-loading.mfp-figure {
  display: none;
}

.mfp-hide {
  display: none !important;
}

.mfp-preloader {
  color: #ccc;
  position: absolute;
  top: 50%;
  width: auto;
  text-align: center;
  margin-top: -0.8em;
  left: 8px;
  right: 8px;
  z-index: 10003;
}

.mfp-preloader a {
  color: #ccc;
}

.mfp-preloader a:hover {
  color: #fff;
}

.mfp-s-ready .mfp-preloader {
  display: none;
}

.mfp-s-error .mfp-content {
  display: none;
}

button.mfp-close,
button.mfp-arrow {
  cursor: pointer;
  border: 0;
  -webkit-appearance: none;
  display: block;
  outline: none;
  padding: 0;
  z-index: 10005;
  box-shadow: none;
  touch-action: manipulation;
}

button::-moz-focus-inner {
  padding: 0;
  border: 0;
}

button::after,
button::before {
  display: none;
}

.mfp-close {
  width: 100%;
  min-width: 3.125rem;
  height: 3.125rem;
  position: fixed;
  right: 0;
  top: 0;
  text-decoration: none;
  text-align: center;
  opacity: 0.65;
  padding: 0 0 1.25rem 0;
  background: transparent url('data:image/svg+xml;utf8,%3Csvg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 21.92 21.92"%3E%3Ctitle%3Eicon--close%3C/title%3E%3Cpolygon points="11.67 10.96 21.92 0.71 21.21 0 10.96 10.25 0.71 0 0 0.71 10.25 10.96 0 21.21 0.71 21.92 10.96 11.67 21.21 21.92 21.92 21.21 11.67 10.96" fill="%23fff"/%3E%3C/svg%3E') top right 0.625rem no-repeat;
  background-size: 1.875rem;
  text-indent: 9999px;
  margin-top: 0.625rem;
}

.mfp-close:hover,
.mfp-close:focus {
  opacity: 1;
  background-color: transparent;
}

@media (min-width: 701px) {
  .mfp-close {
    position: absolute;
    height: 1.875rem;
    margin-top: 0.9375rem;
  }
}

.mfp-counter {
  position: absolute;
  top: 0;
  right: 0;
  color: #ccc;
  font-size: 0.875rem;
  line-height: 1.125rem;
  white-space: nowrap;
  font-family: "Esteban", serif;
  font-weight: bold;
}

.mfp-arrow {
  opacity: 0.65;
  padding: 0.625rem;
  width: 4.375rem;
  height: 70%;
  display: block;
  position: absolute;
  cursor: pointer;
  top: 50%;
  transform: translateY(-50%);
}

.mfp-arrow:hover,
.mfp-arrow:focus {
  opacity: 1;
  background-color: transparent;
}

.mfp-arrow-left {
  left: 0;
  background: transparent url('data:image/svg+xml;utf8,%3Csvg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 30 30"%3E%3Ctitle%3Eicon--arrow-carousel--left%3C/title%3E%3Cpolygon points="20.16 0 20.85 0.51 10.22 15 20.86 29.49 20.16 30 9.14 15 20.16 0" fill="%23fff"/%3E%3C/svg%3E') center center no-repeat;
