    $SIDEBAR
\*------------------------------------ */
","/* ------------------------------------*\
    $FOOTER
\*------------------------------------ */

.c-footer {
  padding-top: $pad*2;
  padding-bottom: $pad*2;
}

.c-footer--inner {
  position: relative;
  overflow: hidden;
}

.c-footer__links {
  display: flex;
  flex-direction: column;
  width: calc(100% - 40px);

  @include media('>medium') {
    width: 100%;
    flex-direction: row;
    justify-content: space-between;
    flex-basis: rem(300);

    > div {
      width: 40%;
      max-width: rem(400);
    }
  }
}

.c-footer__nav {
  @include media('>medium') {
    margin-top: 0 !important;
  }

  &-list {
    column-count: 2;
    column-gap: $space*2;
    column-width: rem(140);

    a {
      @include u-font--secondary--xs;

      color: $white;
      padding-bottom: $pad/2;
      letter-spacing: rem(2.5);
      display: block;

      &:hover {
        color: $gray;
      }
    }
  }
}

.c-footer__scroll {
  width: rem(200);
  height: rem(60);
  display: block;
  transform: rotate(-90deg);
  position: absolute;
  left: auto;
  right: rem(-110);
  top: rem(-10);
  z-index: 4;

  @include media('>medium') {
    top: rem(20);
    left: rem(-70);
    bottom: auto;
    margin: 0 auto !important;
  }

  a {
    width: 100%;
    height: auto;
    display: block;
  }
}

.c-footer__social {
  position: relative;

  &::before,
  &::after {
    content: "";
    display: block;
    height: rem(1);
    background-color: $gray;
    top: 0;
    bottom: 0;
    margin: auto 0;
    width: calc(50% - 52px);
    position: absolute;
  }

  &::before {
    left: 0;
  }

  &::after {
    right: 0;
  }

  &-icons {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: rem(8);
  }

  a {
    display: block;
    width: rem(40);
    height: rem(40);
    border: 1px solid $gray;
    text-align: center;

    .u-icon {
      position: relative;
      top: rem(8);

      svg {
        width: rem(20);
        height: rem(20);
        margin: 0 auto;
      }
    }

    &:hover {
      background-color: $gray;
    }
  }
}

.c-footer__copyright {
  display: flex;
  flex-direction: column;
  margin-top: $space !important;

  @include media('>large') {
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
  }

  @include media('<=large') {
    > div {
      margin-top: $space/2;

      &:first-child {
        margin-top: 0;
      }
    }
  }
}

.c-footer__rights {
  flex: 1;

  @include media('>large') {
    margin-left: $space;
    margin-right: $space;
  }
}
