    $TEXT
\*------------------------------------ */
/* ------------------------------------*\
    $TEXT TYPES
\*------------------------------------ */
/**
 * Text Primary
 */
.u-font--primary--xl,
h1 {
  font-size: 2.5rem;
  line-height: 3.4375rem;
  font-family: "Esteban", serif;
  font-weight: 400; }
  @media (min-width: 901px) {
    .u-font--primary--xl,
    h1 {
      font-size: 3.75rem;
      line-height: 4.6875rem; } }

.u-font--primary--l,
h2 {
  font-size: 1.625rem;
  line-height: 2.25rem;
  font-family: "Esteban", serif;
  font-weight: 400; }
  @media (min-width: 901px) {
    .u-font--primary--l,
    h2 {
      font-size: 2.25rem;
      line-height: 2.875rem; } }

.u-font--primary--m,
h3 {
  font-size: 1.375rem;
  line-height: 1.75rem;
  font-family: "Esteban", serif;
  font-weight: 400; }
  @media (min-width: 901px) {
    .u-font--primary--m,
    h3 {
      font-size: 2rem;
      line-height: 2.375rem; } }

.u-font--primary--s {
  font-size: 1.125rem;
  line-height: 1.375rem;
  font-family: "Esteban", serif; }
  @media (min-width: 901px) {
    .u-font--primary--s {
      font-size: 1.375rem;
      line-height: 1.75rem; } }

/**
 * Text Secondary
 */
.u-font--secondary--s,
h4 {
  font-size: 0.875rem;
  line-height: 1.125rem;
  font-family: "Raleway", sans-serif;
  letter-spacing: 0.1875rem;
  font-weight: 700;
  text-transform: uppercase; }

.u-font--secondary--xs {
  font-size: 0.6875rem;
  line-height: 1.0625rem;
  font-family: "Raleway", sans-serif;
  letter-spacing: 0.125rem;
  font-weight: 700;
  text-transform: uppercase; }
  @media (min-width: 901px) {
    .u-font--secondary--xs {
      font-size: 0.75rem;
      line-height: 1.125rem;
      letter-spacing: 0.1875rem; } }

/**
 * Text Main
 */
.u-font--xl {
  font-size: 1.125rem;
  line-height: 1.875rem;
  font-family: "Esteban", serif; }
  @media (min-width: 901px) {
    .u-font--xl {
      font-size: 1.25rem;
      line-height: 1.875rem; } }

.u-font--l {
  font-size: 1rem;
  line-height: 1.625rem;
  font-family: "Esteban", serif; }
  @media (min-width: 901px) {
    .u-font--l {
      font-size: 1.25rem;
      line-height: 1.875rem; } }

.u-font--m {
  font-size: 1.125rem;
  line-height: 1.75rem;
  font-family: "Esteban", serif;
  font-style: italic; }

.u-font--s {
  font-size: 0.875rem;
  line-height: 1.25rem;
  font-family: "Esteban", serif;
  font-style: italic; }
  @media (min-width: 901px) {
    .u-font--s {
      font-size: 1rem;
      line-height: 1.375rem; } }

/**
 * Text Transforms
 */
.u-text-transform--upper {
  text-transform: uppercase; }

.u-text-transform--lower {
  text-transform: lowercase; }

.u-text-transform--capitalize {
  text-transform: capitalize; }

/**
 * Text Decorations
 */
.u-text-decoration--underline:hover {
  text-decoration: underline; }

/**
 * Font Weights
 */
.u-font-weight--400 {
  font-weight: 400; }

.u-font-weight--700 {
  font-weight: 700; }

.u-font-weight--900 {
  font-weight: 900; }

.u-caption {
  color: #b2adaa;
  padding-top: 0.625rem;
  font-size: 0.875rem;
  line-height: 1.25rem;
  font-family: "Esteban", serif;
  font-style: italic; }
  @media (min-width: 901px) {
    .u-caption {
      font-size: 1rem;
      line-height: 1.375rem; } }

