/* @import must be at top of file, otherwise CSS will not work */
/* ------------------------------------*\
    $FORMS
\*------------------------------------ */
form ol,
form ul {
  list-style: none;
  margin-left: 0; }

legend {
  font-weight: bold;
  margin-bottom: 1.875rem;
  display: block; }

fieldset {
  border: 0;
  padding: 0;
  margin: 0;
  min-width: 0; }

label {
  display: block; }

button,
input,
select,
textarea {
  font-family: inherit;
  font-size: 100%;
  -webkit-appearance: none;
  -webkit-border-radius: 0; }

textarea {
  line-height: 1.5; }

input[type=email],
input[type=number],
input[type=search],
input[type=tel],
input[type=text],
input[type=url],
textarea,
select {
  border: 1px solid #b2adaa;
  background-color: #fff;
  width: 100%;
  outline: 0;
  display: block;
  transition: all 0.5s cubic-bezier(0.885, -0.065, 0.085, 1.02);
  padding: 0.625rem; }

input[type="search"] {
  -webkit-appearance: none;
  border-radius: 0; }

input[type="search"]::-webkit-search-cancel-button,
input[type="search"]::-webkit-search-decoration {
  -webkit-appearance: none; }

/**
 * Form Field Container
 */
.field-container {
  margin-bottom: 1.25rem; }

/**
 * Validation
 */
.has-error {
  border-color: #f00; }

.is-valid {
  border-color: #089e00; }

/* ------------------------------------*\
    $HEADINGS
\*------------------------------------ */
/* ------------------------------------*\
    $LINKS
\*------------------------------------ */
a {
  text-decoration: none;
  color: #24374d;
  transition: all 0.6s ease-out;
  cursor: pointer; }
  a:hover {
    text-decoration: none;
    color: #8d9b86; }
  a p {
    color: #31302e; }

.u-link--cta {
  font-size: 0.875rem;
  line-height: 1.125rem;
  font-family: "Raleway", sans-serif;
  letter-spacing: 0.1875rem;
  font-weight: 700;
  text-transform: uppercase;
  color: #24374d;
  display: table; }
  .u-link--cta .u-icon {
    transition: all 0.25s ease;
    left: 1.25rem;
    position: relative; }
  .u-link--cta:hover .u-icon {
    left: 1.4375rem; }

.u-link--white {
  color: #fff; }
  .u-link--white:hover {
    color: #b2adaa; }
    .u-link--white:hover .u-icon path {
      fill: #b2adaa; }

/* ------------------------------------*\
    $LISTS
\*------------------------------------ */
ol,
ul {
  margin: 0;
  padding: 0;
  list-style: none; }

/**
 * Definition Lists
 */
dl {
  overflow: hidden;
