/**
 * Convert px to rem.
 *
 * @param int $size
 *   Size in px unit.
 * @return string
 *   Returns px unit converted to rem.
 */
@function rem($size) {
  $remSize: $size / $rembase;

  @return #{$remSize}rem;
}

/**
 * Center-align a block level element
 */
@mixin u-center-block {
  display: block;
  margin-left: auto;
  margin-right: auto;
}

/**
 * Standard paragraph
 */
@mixin p {
  font-family: $font;
  font-size: rem(16);
  line-height: rem(26);

  @include media('>medium') {
    font-size: rem(18);
    line-height: rem(28);
  }

  // @include media('>xlarge') {
  //   font-size: rem(20);
  //   line-height: rem(30);
  // }
}

/**
 * Maintain aspect ratio
 */
@mixin aspect-ratio($width, $height) {
  position: relative;

  &::before {
    display: block;
    content: "";
    width: 100%;
    padding-top: ($height / $width) * 100%;
  }

  > .ratio-content {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
  }
}
","@import "tools.mixins";

/* ------------------------------------*\
    $VARIABLES
\*------------------------------------ */

/**
 * Grid & Baseline Setup
 */
$fontpx: 16; // Font size (px) baseline applied to <body> and converted to %.
$defaultpx: 16; // Browser default px used for media queries
$rembase: 16; // 16px = 1.00rem
$max-width-px: 1200;
$max-width: rem($max-width-px) !default;

/**
 * Colors
 */
$white: #fff;
$black: #31302e;
$gray: #b2adaa;
$error: #f00;
$valid: #089e00;
$warning: #fff664;
$information: #000db5;
$green: #8d9b86;
$blue: #24374d;
$red: #f53d31;
$tan: #f5f4ed;

/**
 * Style Colors
 */
$primary-color: $green;
$secondary-color: $blue;
$tertiary-color: $red;
$background-color: $white;
$link-color: $secondary-color;
$link-hover: $primary-color;
$button-color: $tertiary-color;
$button-hover: darken($tertiary-color, 10%);
$body-color: $black;
$border-color: $gray;
$overlay: rgba(25, 25, 25, 0.6);

/**
 * Typography
 */
$font: 'Esteban', serif;
$font-primary: 'Esteban', serif;
$font-secondary: 'Raleway', sans-serif;
$sans-serif: "Helvetica", sans-serif;
$serif: Georgia, Times, "Times New Roman", serif;
$monospace: Menlo, Monaco, "Courier New", "Courier", monospace;

// Questa font weights: 400 700 900

/**
 * Amimation
 */
$cubic-bezier: cubic-bezier(0.885, -0.065, 0.085, 1.02);
$ease-bounce: cubic-bezier(0.3, -0.14, 0.68, 1.17);

/**
 * Default Spacing/Padding
 */
$space: 1.25rem;
$space-and-half: $space*1.5;
$space-double: $space*2;
$space-quad: $space*4;
$space-half: $space/2;
$pad: 1.25rem;
$pad-and-half: $pad*1.5;
$pad-double: $pad*2;
$pad-half: $pad/2;
$pad-quarter: $pad/4;
$pad-quad: $pad*4;
$gutters: (mobile: 10, desktop: 10, super: 10);
$verticalspacing: (mobile: 20, desktop: 30);

/**
 * Icon Sizing
 */
$icon-xsmall: rem(10);
$icon-small: rem(20);
$icon-medium: rem(25);
$icon-large: rem(60);
$icon-xlarge: rem(60);

/**
 * Common Breakpoints
 */
$xsmall: 350px;
$small: 500px;
$medium: 700px;
$large: 900px;
$xlarge: 1100px;
$xxlarge: 1300px;
$xxxlarge: 1500px;

$breakpoints: (
  'xsmall': $xsmall,
  'small': $small,
  'medium': $medium,
  'large': $large,
  'xlarge': $xlarge,
  'xxlarge': $xxlarge,
  'xxxlarge': $xxxlarge
);

/**
 * Element Specific Dimensions
 */
$nav-width: rem(260);
$article-max: rem(1200);
$sidebar-width: 320;
$small-header-height: rem(60);
$large-header-height: rem(80);
","/* ------------------------------------*\
    $MEDIA QUERY TESTS
\*------------------------------------ */
@if $tests == true {
  body {
    &::before {
      display: block;
      position: fixed;
      z-index: 100000;
      background: black;
      bottom: 0;
      right: 0;
      padding: 0.5em 1em;
      content: 'No Media Query';
      color: transparentize(#fff, 0.25);
      border-top-left-radius: 10px;
      font-size: (12/16)+em;

      @media print {
        display: none;
      }
    }

    &::after {
      display: block;
      position: fixed;
      height: 5px;
      bottom: 0;
      left: 0;
      right: 0;
      z-index: (100000);
      content: '';
      background: black;

      @media print {
        display: none;
      }
    }

    @include media('>xsmall') {
      &::before {
        content: 'xsmall: 350px';
      }

      &::after,
      &::before {
        background: dodgerblue;
      }
    }

    @include media('>small') {
      &::before {
        content: 'small: 500px';
      }

      &::after,
      &::before {
        background: darkseagreen;
      }
    }

    @include media('>medium') {
      &::before {
        content: 'medium: 700px';
      }

      &::after,
