","/* ------------------------------------*\
    $LINKS
\*------------------------------------ */
a {
  text-decoration: none;
  color: $link-color;
  transition: all 0.6s ease-out;
  cursor: pointer;

  &:hover {
    text-decoration: none;
    color: $link-hover;
  }

  p {
    color: $body-color;
  }
}

.u-link--cta {
  @include u-font--secondary--s;

  color: $link-color;
  display: table;

  .u-icon {
    transition: all 0.25s ease;
    left: $space;
    position: relative;
  }

  &:hover {
    .u-icon {
      left: rem(23);
    }
  }
}

.u-link--white {
  color: $white;

  &:hover {
    color: $gray;

    .u-icon {
      path {
        fill: $gray;
      }
    }
  }
}
","/* ------------------------------------*\
    $LISTS
\*------------------------------------ */
ol,
ul {
  margin: 0;
  padding: 0;
  list-style: none;
}

/**
 * Definition Lists
 */
dl {
  overflow: hidden;
  margin: 0 0 $space;
}

dt {
  font-weight: bold;
}

dd {
  margin-left: 0;
}
","/* ------------------------------------*\
    $SITE MAIN
\*------------------------------------ */

body {
  background: $background-color;
  font: 400 100%/1.3 $font;
  -webkit-text-size-adjust: 100%;
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  color: $body-color;
  overflow-x: hidden;
}
","/* ------------------------------------*\
    $MEDIA ELEMENTS
\*------------------------------------ */

/**
 * Flexible Media
 */
iframe,
img,
object,
svg,
video {
  max-width: 100%;
  border: none;
}

img[src$=".svg"] {
  width: 100%;
}

picture {
  display: block;
  line-height: 0;
}

figure {
  max-width: 100%;

  img {
    margin-bottom: 0;
  }
}

.fc-style,
figcaption {
  font-size: rem(14);
  padding-top: rem(3);
  margin-bottom: rem(5);
}

.clip-svg {
  height: 0;
}

/* ------------------------------------*\
    $PRINT STYLES
\*------------------------------------ */
@media print {
  *,
  *::after,
  *::before,
  *::first-letter,
  *::first-line {
    background: transparent !important;
    color: $black !important;
    box-shadow: none !important;
    text-shadow: none !important;
  }

  a,
  a:visited {
    text-decoration: underline;
  }

  a[href]::after {
    content: " (" attr(href) ")";
  }

  abbr[title]::after {
    content: " (" attr(title) ")";
  }

  /*
   * Don't show links that are fragment identifiers,
   * or use the `javascript:` pseudo protocol
   */
  a[href^="#"]::after,
  a[href^="javascript:"]::after {
    content: "";
  }

  blockquote,
  pre {
    border: 1px solid $border-color;
    page-break-inside: avoid;
  }

  /*
   * Printing Tables:
   * http://css-discuss.incutio.com/wiki/Printing_Tables
   */
  thead {
    display: table-header-group;
  }

  img,
  tr {
    page-break-inside: avoid;
  }

  img {
    max-width: 100% !important;
  }

  h2,
  h3,
  p {
    orphans: 3;
    widows: 3;
  }

  h2,
  h3 {
    page-break-after: avoid;
  }

  #footer,
  #header,
  .ad,
  .no-print {
    display: none;
  }
}
","/* ------------------------------------*\
    $TABLES
\*------------------------------------ */
table {
  border-collapse: collapse;
  border-spacing: 0;
  border: 1px solid $border-color;
  width: 100%;
}

th {
  text-align: left;
  border: 1px solid $border-color;
  padding: 0.2em;
}

td {
  border: 1px solid $border-color;
  padding: 0.2em;
}
","/* ------------------------------------*\
    $TEXT ELEMENTS
\*------------------------------------ */

/**
 * Abstracted paragraphs
 */
p,
ul,
ol,
dt,
dd,
pre {
  @include p;
}

/**
 * Bold
 */
b,
strong {
  font-weight: 700;
}

/**
 * Horizontal Rule
 */
hr {
  height: 1px;
  border: none;
  background-color: $border-color;

  @include u-center-block;
}

/**
 * Abbreviation
 */
abbr {
  border-bottom: 1px dotted $border-color;
  cursor: help;
}
","/* ------------------------------------*\
    $GRIDS
\*------------------------------------ */

/**
 * Simple grid - keep adding more elements to the row until the max is hit
 * (based on the flex-basis for each item), then start new row.
 */
.l-grid {
  display: flex;
  display: inline-flex;
  flex-flow: row wrap;
}

/**
 * Fixed Gutters
 */
@mixin column-gutters() {
  padding-left: $pad/1.5;
  padding-right: $pad/1.5;

  @include media ('>xlarge') {
    &.u-left-gutter--l {
      padding-left: rem(30);
    }

    &.u-right-gutter--l {
      padding-right: rem(30);
    }

    &.u-left-gutter--xl {
      padding-left: rem(60);
    }

    &.u-right-gutter--xl {
      padding-right: rem(60);
    }
  }
}

[class*="grid--"] {
  &.u-no-gutters {
    margin-left: 0;
    margin-right: 0;

    > .l-grid-item {
      padding-left: 0;
      padding-right: 0;
    }
  }

  > .l-grid-item {
    box-sizing: border-box;

    @include column-gutters();
  }
}

@mixin layout-in-column {
  margin-left: -1 * $space/1.5;
  margin-right: -1 * $space/1.5;

  @include media ('>xlarge') {
    margin-left: -1 * $space/1.5;
    margin-right: -1 * $space/1.5;
  }
}

[class*="l-grid--"] {
  @include layout-in-column;
}

.l-grid-item {
  width: 100%;
  box-sizing: border-box;
}

/**
* 1 to 2 column grid at 50% each.
*/
.l-grid--50-50 {
  @include media ('>medium') {
    width: 100%;

    > * {
      width: 50%;
    }
  }
}

/**
 * 3 column grid
 */
.l-grid--3-col {
  margin: 0;

  @include media ('>medium') {
    width: 100%;

    > * {
      width: 33.3333%;
    }
  }
}

/**
 * 4 column grid
 */
.l-grid--4-col {
  > * {
    margin-bottom: $space*1.5;
    display: flex;
    align-items: stretch;
  }

  @include media('>small') {
    > * {
      width: 50%;
    }
  }

  @include media('>medium') {
    width: 100%;
  }

  @include media('>large') {
    > * {
      width: 25%;
    }
  }
}

.l-grid--photos {
  column-count: 2;
  -moz-column-gap: $space;
  -webkit-column-gap: $space;
  column-gap: $space;
  display: block;
  padding: 0;
  margin: 0;

  > .l-grid-item {
    display: block;
    margin: 0 auto;
    padding: 0;
    margin-bottom: $space;
    width: 100%;
  }

  @include media('>small') {
    column-count: 3;
  }

  @include media('>medium') {
    column-count: 4;
  }

  @include media('>xxlarge') {
    column-count: 5;
  }
}
","/* ------------------------------------*\
    $WRAPPERS & CONTAINERS
\*------------------------------------ */

/**
 * Layout containers - keep content centered and within a maximum width. Also
 * adjusts left and right padding as the viewport widens.
 */
.l-container {
  margin: 0 auto;
  position: relative;
  padding-left: $pad;
  padding-right: $pad;

  @include media('>xlarge') {
    padding-left: $pad*2;
    padding-right: $pad*2;
  }
}

/**
 * Wrapping element to keep content contained and centered.
 */
.l-wrap {
  max-width: $max-width;
  margin: 0 auto;
}

/**
 * Wrapping element to keep content contained and centered at narrower widths.
 */
.l-narrow {
  max-width: rem(800);
  margin: 0 auto;
}

.l-narrow--xs {
  max-width: rem(500);
}

.l-narrow--s {
  max-width: rem(600);
}

.l-narrow--m {
  max-width: rem(700);
}

.l-narrow--l {
  max-width: $article-max;
}

.l-narrow--xl {
  max-width: rem(1300);
}
","/* ------------------------------------*\
    $BLOCKS
\*------------------------------------ */

.single-product,
.template-shop {
  .c-block__thumb {
    background: white;
    min-height: rem(200);
    position: relative;
    border-bottom: 1px solid $border-color;

    img {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      bottom: 0;
      display: block;
      max-height: 80%;
      margin: auto;
      width: auto;
    }
  }
}

.c-block__default {
  .l-grid {
    margin: 0;
    display: flex;
  }

  .c-block__media {
    min-height: rem(250);
    background-color: $tan;
    background-size: cover;

    @include media('>large') {
      min-height: rem(300);
    }
  }

  .c-block__content {
    text-decoration: none;
    display: flex;
    justify-content: space-between;
    flex-direction: column;
  }
}

.c-block__link {
  &:hover {
    color: inherit;
  }
}

.c-block-news {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  min-height: rem(400);
  width: 100%;

  .c-block__button {
    display: flex;
    justify-content: space-between;
    border-top: 1px solid $border-color;
  }

  .c-block__link {
    position: relative;
  }

  .c-block__title,
  .c-block__date,
  .c-block__excerpt {
    font-weight: normal;
  }

  .c-block__date,
  .c-block__excerpt {
    color: $black;
  }

  .c-block__title {
    color: $secondary-color;
  }

  .c-block__link,
  .c-block__content {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    height: 100%;
    align-items: stretch;
    transition: all 0.25s ease-in-out;
    top: auto;
  }

  &.has-hover {
    .c-block__excerpt {
      display: none;
    }
  }
}

.touch .c-block-news {
  .c-block__excerpt {
    display: block;
  }
}

.no-touch .c-block-news:hover {
  .c-block__content {
    position: absolute;
    top: 0;
    background: $tan;
    width: 100%;
  }

  .c-block__excerpt {
    display: block;
  }

  .c-block__button {
    background-color: $primary-color;
    color: white;

    .u-icon path {
      fill: $white;
    }
  }
}

.c-block-events {
  .c-block__link {
    display: flex;
    flex-direction: column;
    overflow: hidden;
    border: 1px solid $black;
    margin-bottom: $space;
    position: relative;

    @include media('>small') {
      flex-direction: row;
      height: rem(200);
      margin-top: rem(-1);
      margin-bottom: 0;
    }

    &.disable {
      pointer-events: none;

      .u-icon {
        display: none;
      }
    }
  }

  .c-block__day {
    position: relative;
    display: block;
    width: 100%;

    @include media('>small') {
      width: rem(40);
      height: auto;
    }

    &::after {
      @include u-font--secondary--s;

      content: attr(data-content);
      text-align: center;
      display: block;
      color: $gray;
      line-height: rem(40);
      width: 100%;
      height: rem(40);
      background-color: $black;

      @include media('>small') {
        background-color: transparent;
        transform: rotate(-90deg);
        width: rem(200);
        height: rem(200);
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
      }
    }
  }

  .c-block__date {
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: $pad;
    position: absolute;
    top: rem(40);
    background-color: $primary-color;
    color: $white;
    z-index: 1;

    @include media('>small') {
      position: relative;
      top: auto;
      border-right: 1px solid $black;
      background-color: $white;
      color: $black;
      min-width: rem(80);
    }
  }

  .c-block__date + .c-block__content {
    @include media('<=small') {
      padding-left: rem(100);
    }
  }

  .c-block__media {
    position: relative;
    min-height: rem(250);

    @include media('>small') {
      width: rem(500);
      height: 100%;
      min-height: auto;
      display: block;
    }
  }

  .c-block__content {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-direction: column;
    width: 100%;

    @include media('>small') {
      flex: auto;
      flex-direction: row;
    }
  }

  .c-block__header {
    width: 100%;
    justify-content: flex-start;
    text-align: left;
    display: flex;
    flex-direction: column;
    flex: auto;

    @include media('>small') {
      padding-right: $pad*2;
    }
  }

  .c-block__excerpt {
    @include media('>small') {
      overflow: hidden;
      text-overflow: ellipsis;
      display: -webkit-box;
      -webkit-line-clamp: 2;
      -webkit-box-orient: vertical;
    }
  }

  .u-icon {
    display: none;
    height: rem(11);
    position: relative;
    right: $space/2;
    transition: right 0.25s ease-in-out;

    @include media('>small') {
      display: inline-block;
    }
  }

  &:hover {
    .u-icon {
      right: 0;
    }
  }
}

.c-block-featured-page {
  position: relative;
  padding: 0 !important;
  margin: 0;
  overflow: hidden;

  .c-block__content {
    display: flex;
    justify-content: space-between;
    flex-direction: column;
    min-height: rem(300);
    z-index: 1;

    @include media('>medium') {
      min-height: rem(400);
    }

    @include media('>large') {
      min-height: rem(550);
    }
  }

  .c-block__media {
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    margin: auto;
    width: 110%;
    height: 110%;
    z-index: -1;
    transform: scale(1);
    transition: transform 0.25s ease;
  }

  &:hover {
    .c-block__media {
      -webkit-filter: blur(2px);
      filter: blur(2px);
      transform: scale(1.1);
    }

    .o-button {
      background-color: $button-color;
      border-color: $button-color;
    }
  }
}

.c-block-gallery {
  &__images {
    display: flex;
    flex-direction: column;

    img {
      width: 100%;
      height: auto;
      display: block;
    }
  }

  &__image {
    figure {
      position: relative;
      width: 100%;
      height: auto;
    }

    figure a > figcaption {
      position: absolute;
      bottom: 0;
      left: 0;
      width: 100%;
      height: auto;
      max-width: 100%;
      padding: $space / 4;
      background-color: rgba(black, 0.6);
      color: white;
    }
  }

  &__image-link {
    cursor: zoom-in;
  }

  &__nav {
    margin-top: $space / 4;
    margin-left: -$space / 4;
    margin-right: -$space / 4;

    picture {
      cursor: pointer;
      padding: $space / 4;
    }

    .slick-slide:focus {
      border-radius: 0;
      outline: 0;
      box-shadow: inset 0 0 0 2px $primary-color;
    }
  }
}
","/* ------------------------------------*\
