.u-list__details {
  border-left: 1px solid $gray;
  padding-left: $pad;
}
","/* ------------------------------------*\
    $NAVIGATION
\*------------------------------------ */

.c-nav__primary {
  position: absolute;
  top: $small-header-height;
  left: 0;
  width: 100%;
  background-color: $tan;
  box-shadow: 0 2px 0 rgba($gray, 0.4);
  transition: none;

  @include media('>xlarge') {
    position: relative;
    top: 0;
    background-color: transparent;
    box-shadow: none;
    width: auto;
  }

  &.this-is-active {
    .c-primary-nav__list {
      display: block;
    }

    .c-nav__toggle {
      .c-nav__toggle-span--1 {
        opacity: 0;
      }

      .c-nav__toggle-span--2 {
        transform: rotate(45deg);
        top: rem(-4);
        right: rem(-2);
      }

      .c-nav__toggle-span--3 {
        transform: rotate(-45deg);
        top: rem(-10);
        right: rem(-2);
      }

      .c-nav__toggle-span--4::after {
        content: "Close";
      }
    }
  }
}

.c-nav__toggle {
  position: absolute;
  padding: $pad;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: $small-header-height;
  width: $small-header-height;
  top: -$small-header-height;
  right: 0;

  @include media('>xlarge') {
    display: none;
  }

  .c-nav__toggle-span {
    display: block;
    background-color: $white;
    width: rem(30);
    height: rem(1);
    margin-bottom: rem(5);
    transition: transform 0.25s ease;
    position: relative;
    border: 0;
    outline: 0;
  }

  .c-nav__toggle-span--4 {
    margin: 0;
    background-color: transparent;
    height: auto;
    color: $white;
    display: block;

    &::after {
      position: absolute;
      top: 0;
      left: 0;
      right: 0;
      margin: 0 auto;
      text-align: center;
      content: "Menu";
      padding-top: rem(3);
      font-family: $font-secondary;
      text-transform: uppercase;
      font-weight: 700;
      line-height: rem(3);
      letter-spacing: rem(1.25);
      font-size: 9px;
    }
  }
}

.c-primary-nav__list {
  height: auto;
  width: 100%;
  display: none;

  @include media('>xlarge') {
    display: flex;
    flex-direction: row;
  }

  &-toggle {
    border-bottom: 1px solid rgba($border-color, 0.4);
    display: flex;
    justify-content: space-between;
    align-items: center;
    position: relative;
    cursor: pointer;

    @include media('>xlarge') {
      border: 0;
      height: $large-header-height;
    }

    a {
      width: calc(100% - 50px);
      padding: $pad/2 $pad/2;
      font-weight: 700;

      @include media('>xlarge') {
        width: auto;
      }

      @include media('>xxlarge') {
        padding: $pad;
      }
    }

    span {
      display: none;
      position: relative;
      height: 100%;
      width: rem(50);
      padding: $pad/4 $pad/2;
      text-align: right;
      cursor: pointer;

      svg {
        width: rem(15);
        height: rem(15);
        right: 0;
        top: rem(3);
        position: relative;
      }
    }
  }

  &-item {
    position: relative;
    cursor: pointer;

    &.active {
      @include media('>xlarge') {
        background-color: lighten($primary-color, 5%);
      }
    }

    &.this-is-active {
      background-color: darken($tan, 10%);

      @include media('>xlarge') {
        background-color: lighten($primary-color, 5%);
      }

      .c-primary-nav__list-toggle span svg {
        transform: rotate(90deg);
        right: rem(22);
      }

      .c-sub-nav__list {
        display: block;
      }
    }

    &.has-sub-nav {
      .c-primary-nav__list-link {
        @include media('>xlarge') {
          transition: none;
          font-size: rem(16);
        }
      }

      .c-primary-nav__list-toggle {
        position: relative;

        span {
          display: block;
          height: rem(38);
          width: rem(60);
          padding: 0;
          position: absolute;
          right: 0;
          top: 0;
          border-left: 1px solid rgba($border-color, 0.4);
          z-index: 999;

          svg {
            right: rem(21);
            top: rem(9);
          }

          @include media('>xlarge') {
            display: none;
          }
        }
      }
    }
  }

  &-link {
    @include media('>xlarge') {
      font-size: rem(12);
      letter-spacing: rem(2);
      white-space: nowrap;
      color: $white;

      &:hover {
        color: $white;
      }
    }
  }
}

.c-sub-nav__list {
  background-color: $white;
  display: none;

  @include media('>xlarge') {
    position: absolute;
    left: 0;
    width: max-content;
    box-shadow: 0 1px 2px rgba($gray, 0.5);
  }

  &-item {
    &.active {
      @include media('>xlarge') {
        background-color: $tan;
      }
    }
  }

  &-link {
    @include p;

    padding: $pad/4 $pad;
    display: block;
    width: 100%;
    border-bottom: 1px solid rgba($border-color, 0.4);

    &:hover {
      background-color: $tan;
      color: $secondary-color;
    }
  }
}

.c-secondary-nav {
  &__list {
    display: flex;
    flex-direction: row;
    flex-wrap: wrap;
    justify-content: center;
  }

  &__link {
    padding: 0 $pad/2;
    color: $secondary-color;

    &.this-is-active {
      color: $primary-color;
    }
  }
}

.c-breadcrumbs {
  span {
    color: $gray;
  }
}
","/* ------------------------------------*\
    $PAGE SECTIONS
\*------------------------------------ */

.c-section {
  padding: $pad*2 0;

  @include media('>medium') {
    padding: $pad*4 0;
  }

  &__blocks {
    padding-top: 0;
  }
}

.c-slideshow {
  &__image {
    position: relative;
    min-height: 70vh;
    z-index: 0;
  }

  &__content {
    z-index: 1;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    margin: auto;
    display: flex;
    justify-content: center;
    align-items: center;
  }
}

.c-section-hero {
  display: flex;
  justify-content: center;
  align-items: center;
  height: auto;

  &::after {
    z-index: 1 !important;
  }

  &--short {
    min-height: rem(250);

    @include media('>medium') {
      min-height: rem(350);
    }

    @include media('>xlarge') {
      min-height: rem(450);
    }
  }

  &--tall {
    min-height: rem(350);

    @include media('>medium') {
      min-height: 70vh;
    }
  }

  &__content {
    position: relative;
    z-index: 2;
  }

  &__caption {
    position: absolute;
    z-index: 999;
    bottom: rem(5);
    left: rem(5);
  }
}

.c-section-events {
  &__title {
    position: relative;
    z-index: 1;

    &::after {
      content: "Upcoming";
      font-size: rem(144);
      line-height: 1;
      color: $white;
      opacity: 0.1;
      position: absolute;
      z-index: 0;
      top: rem(-72);
      left: 0;
      right: 0;
      bottom: 0;
      margin: auto;
      display: none;

      @include media('>medium') {
        display: block;
      }

      .c-section-past-events & {
        content: "Past";
      }
    }
  }

  &__feed {
    z-index: 2;
  }
}

.c-section-news {
  &__title {
    position: relative;
    z-index: 1;
    margin-top: $space*2;

    @include media('>medium') {
      margin-top: $space*4;
    }

    &::after {
      content: "Stay in the Loop";
      font-size: rem(144);
      line-height: 1;
      color: $white;
      opacity: 0.1;
      position: absolute;
      z-index: 0;
      top: rem(-72);
      left: 0;
      right: 0;
      bottom: 0;
      margin: auto;
      display: none;

      @include media('>medium') {
        display: block;
      }
    }

    + .c-section {
      z-index: 2;
      padding-top: $pad*2;
    }
  }
}

.c-section-related {
  padding-bottom: $pad;
}

.c-section__featured-pages {
  position: relative;

  &::after {
    content: "";
    display: block;
    width: 100%;
    height: 100%;
    z-index: -2;
    background: $secondary-color;
    position: absolute;
    top: 0;
    left: 0;
  }
}
","/* ------------------------------------*\
    $SPECIFIC FORMS
\*------------------------------------ */

/* Chrome/Opera/Safari */
::-webkit-input-placeholder {
  color: $gray;
}

/* Firefox 19+ */
::-moz-placeholder {
  color: $gray;
}

/* IE 10+ */
:-ms-input-placeholder {
  color: $gray;
}

/* Firefox 18- */
:-moz-placeholder {
  color: $gray;
}

label {
  margin-top: $space;
  width: 100%;

  @include u-font--secondary--xs;
}

select {
  -webkit-appearance: none;
  -moz-appearance: none;
  appearance: none;
  cursor: pointer;
  background: url('data:image/svg+xml;utf8,%3Csvg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 10 10"%3E%3Ctitle%3EArrow Down%3C/title%3E%3Cpath d="M5.38,7.5,9.62,3.25a.5.5,0,1,0-.71-.71L5,6.44,1.09,2.5a.5.5,0,0,0-.71,0,.51.51,0,0,0,0,.71L4.62,7.45h0a.5.5,0,0,0,.7.1Z" fill="%2331302e"/%3E%3C/svg%3E') $white center right rem(10) no-repeat;
  background-size: rem(10);
}

input[type=email],
input[type=number],
input[type=search],
input[type=tel],
input[type=text],
input[type=url],
textarea,
select {
  width: 100%;
  font-size: rem(16);

  &:focus {
    border-color: $secondary-color;
  }
}

input[type=checkbox],
input[type=radio] {
  outline: none;
  border: none;
  margin: 0 rem(5) 0 0;
  height: rem(20);
  width: rem(20);
  line-height: rem(20);
  background-size: rem(20);
  background-repeat: no-repeat;
  background-position: center center;
  cursor: pointer;
  display: block;
  float: left;
  -webkit-touch-callout: none;
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  -webkit-appearance: none;
  background-color: $white;
  position: relative;
  top: rem(3);
  border-width: 1px;
  border-style: solid;
  border-color: $border-color;
}

input[type=radio] {
  border-radius: rem(50);
}

input[type=checkbox]:checked {
  border-color: $secondary-color;
  background: $secondary-color url('data:image/svg+xml;utf8,%3Csvg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"%3E%3Ctitle%3Eo-icon--check%3C/title%3E%3Cpath d="M15.71,3.31,8.41,14,4.2,8.91,2.66,10.19l5.91,7.18L17.35,4.44Z" fill="%23fff"/%3E%3C/svg%3E') center center no-repeat;
}

input[type=radio]:checked {
  border-color: $secondary-color;
  background: $secondary-color url('data:image/svg+xml;utf8,%3Csvg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"%3E%3Ctitle%3Eo-icon--radio%3C/title%3E%3Ccircle cx="10" cy="10" r="5.63" fill="%23fff"/%3E%3C/svg%3E') center center no-repeat;
}

input[type=checkbox] + span,
input[type=radio] + span {
  display: inline-block;
  cursor: pointer;
  position: relative;
}

input[type=submit] {
  color: $white;
  padding-right: $pad;
  cursor: pointer;
}

.o-filter-select {
  padding: 0;
  border: 0;
  outline: 0;
  color: $secondary-color;
  width: rem(125);
  margin-left: $space;

  @include p;
}

.wpforms-field-sublabel {
  margin-top: 0;
  margin-bottom: $space;
  text-transform: none;
  letter-spacing: normal;
  font-weight: normal;

  @include u-font--s;
  @include p;
}
","/* Slider */
.slick-slider {
  position: relative;
  display: block;
  box-sizing: border-box;
  -webkit-touch-callout: none;
  -webkit-user-select: none;
  -khtml-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
  -ms-touch-action: pan-y;
  touch-action: pan-y;
  -webkit-tap-highlight-color: transparent;
}

.slick-list {
  position: relative;
  overflow: hidden;
  display: block;
  margin: 0;
  padding: 0;

  &:focus {
    outline: none;
  }

  &.dragging {
    cursor: pointer;
    cursor: hand;
  }
}

.slick-slider .slick-list,
.slick-slider .slick-track {
  -webkit-transform: translate3d(0, 0, 0);
  -moz-transform: translate3d(0, 0, 0);
  -ms-transform: translate3d(0, 0, 0);
  -o-transform: translate3d(0, 0, 0);
  transform: translate3d(0, 0, 0);
}

.slick-track {
  position: relative;
  left: 0;
  top: 0;
  display: block;
  margin-left: auto;
  margin-right: auto;

  &::after,
  &::before {
    content: "";
    display: table;
  }

  &::after {
    clear: both;
  }

  .slick-loading & {
    visibility: hidden;
  }
}

.slick-slide {
  float: left;
  height: 100%;
  min-height: 1px;

  [dir="rtl"] & {
    float: right;
  }

  img {
    display: block;
  }

  &.slick-loading img {
    display: none;
  }

  display: none;

  &.dragging img {
    pointer-events: none;
  }

  .slick-initialized & {
    display: block;
  }

  .slick-loading & {
    visibility: hidden;
  }

  .slick-vertical & {
    display: block;
    height: auto;
    border: 1px solid transparent;
  }
}

.slick-arrow.slick-hidden {
  display: none;
}

.slick-slideshow {
  .slick-slide {
    visibility: hidden;
    opacity: 0;
    background-color: $black !important;
    z-index: -1;
    transition: all 0.5s cubic-bezier(0.28, 0, 0.18, 1) !important;

    &.slick-active {
      z-index: 1;
      visibility: visible;
      opacity: 1 !important;
    }
  }

  &.slick-slider .slick-background {
    transition: transform 11.5s cubic-bezier(0.28, 0, 0.18, 1);
    transition-delay: 0.25s;
    transform: scale(1.1, 1.1);
  }

  &.slick-slider .slick-active > .slick-background {
    transform: scale(1.001, 1.001) translate3d(0, 0, 0);
    transform-origin: 50% 50%;
  }
}

.slick-arrow {
  display: block;
  width: rem(60);
  height: rem(60);
  background-color: $black;
  position: absolute;
  top: 50%;
  z-index: 99;
  cursor: pointer;
  transform: translateY(-50%);
  transition: all 0.25s ease;

  &:hover {
    background-color: $secondary-color;
  }

  @include media('<=small') {
    display: none !important;
  }
}

.slick-gallery .slick-dots {
  height: rem(40);
  line-height: rem(40);
  width: 100%;
  list-style: none;
  text-align: center;

  li {
    position: relative;
    display: inline-block;
    margin: 0;
    padding: 0 rem(5);
    cursor: pointer;

    button {
      padding: 0;
      border-radius: rem(50);
      border: 0;
      display: block;
      height: rem(10);
      width: rem(10);
      outline: none;
      line-height: 0;
      font-size: 0;
      color: transparent;
      background: $gray;
    }

    &::before,
    button::after {
      display: none !important;
    }

    &.slick-active {
      button {
        background-color: $secondary-color;
      }
    }
  }
}

////////////////////////
//      Settings      //
////////////////////////

// overlay
$mfp-overlay-color:                   #0b0b0b !default;                    // Color of overlay screen
$mfp-overlay-opacity:                 0.8 !default;                        // Opacity of overlay screen
$mfp-shadow:                          0 0 8px rgba(0, 0, 0, 0.6) !default; // Shadow on image or iframe

// spacing
$mfp-popup-padding-left:              8px !default;                        // Padding from left and from right side
$mfp-popup-padding-left-mobile:       6px !default;                        // Same as above, but is applied when width of window is less than 800px

$mfp-z-index-base:                    9999 !default;                       // Base z-index of popup

// controls
$mfp-include-arrows:                  true !default;                       // Include styles for nav arrows
$mfp-controls-opacity:                0.65 !default;                       // Opacity of controls
$mfp-controls-color:                  #fff !default;                       // Color of controls
$mfp-controls-border-color:           #3f3f3f !default; 	                 // Border color of controls
$mfp-inner-close-icon-color:          #333 !default;                       // Color of close button when inside
$mfp-controls-text-color:             #ccc !default;                       // Color of preloader and "1 of X" indicator
$mfp-controls-text-color-hover:       #fff !default;                       // Hover color of preloader and "1 of X" indicator

// Iframe-type options
$mfp-include-iframe-type:             true !default;                       // Enable Iframe-type popups
$mfp-iframe-padding-top:              40px !default;                       // Iframe padding top
$mfp-iframe-background:               #000 !default;                       // Background color of iframes
$mfp-iframe-max-width:                900px !default;                      // Maximum width of iframes
$mfp-iframe-ratio:                    9/16 !default;                       // Ratio of iframe (9/16 = widescreen, 3/4 = standard, etc.)

// Image-type options
$mfp-include-image-type:              true !default;                       // Enable Image-type popups
$mfp-image-background:                #444 !default;
$mfp-image-padding-top:               60px !default;                       // Image padding top
$mfp-image-padding-bottom:            60px !default;                       // Image padding bottom
$mfp-include-mobile-layout-for-image: true !default;                       // Removes paddings from top and bottom

// Image caption options
$mfp-caption-title-color:             #f3f3f3 !default;                    // Caption title color
$mfp-caption-subtitle-color:          #bdbdbd !default;                    // Caption subtitle color

// A11y
$mfp-use-visuallyhidden:              false !default;                      // Hide content from browsers, but make it available for screen readers

/* Magnific Popup CSS */

////////////////////////
//
// Contents:
//
// 1. General styles
//    - Transluscent overlay
//    - Containers, wrappers
//    - Cursors
//    - Helper classes
// 2. Appearance
//    - Preloader & text that displays error messages
//    - CSS reset for buttons
//    - Close icon
//    - "1 of X" counter
//    - Navigation (left/right) arrows
//    - Iframe content type styles
//    - Image content type styles
//    - Media query where size of arrows is reduced
//    - IE7 support
//
////////////////////////

////////////////////////
// 1. General styles
////////////////////////

// Transluscent overlay
.mfp-bg {
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: $mfp-z-index-base + 2;
  overflow: hidden;
  position: fixed;
  background: $mfp-overlay-color;
  opacity: $mfp-overlay-opacity;
}

// Wrapper for popup
.mfp-wrap {
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  z-index: $mfp-z-index-base + 3;
  position: fixed;
  outline: none !important;
  -webkit-backface-visibility: hidden; // fixes webkit bug that can cause "false" scrollbar
}

// Root container
.mfp-container {
  text-align: center;
  position: absolute;
  width: 100%;
  height: 100%;
  left: 0;
  top: 0;
  padding: 0 $mfp-popup-padding-left;
  box-sizing: border-box;

  &::before {
    content: '';
    display: inline-block;
    height: 100%;
    vertical-align: middle;
  }
}

// Remove vertical centering when popup has class `mfp-align-top`
.mfp-align-top {
  .mfp-container {
    &::before {
      display: none;
    }
  }
}

// Popup content holder
.mfp-content {
  position: relative;
  display: inline-block;
  vertical-align: middle;
  margin: 0 auto;
  text-align: left;
  z-index: $mfp-z-index-base + 5;
}

.mfp-inline-holder,
.mfp-ajax-holder {
  .mfp-content {
    width: 100%;
    cursor: auto;
  }
}

// Cursors
.mfp-ajax-cur {
  cursor: progress;
}

.mfp-zoom-out-cur {
  overflow: hidden;

  &,
  .mfp-image-holder .mfp-close {
    cursor: -moz-zoom-out;
    cursor: -webkit-zoom-out;
    cursor: zoom-out;
  }
}

.mfp-zoom {
  cursor: pointer;
  cursor: -webkit-zoom-in;
  cursor: -moz-zoom-in;
  cursor: zoom-in;
}

.mfp-auto-cursor {
  .mfp-content {
    cursor: auto;
  }
}

.mfp-close,
.mfp-arrow,
.mfp-preloader,
.mfp-counter {
  user-select: none;
}

// Hide the image during the loading
.mfp-loading {
  &.mfp-figure {
    display: none;
  }
}

// Helper class that hides stuff
@if $mfp-use-visuallyhidden {
  // From HTML5 Boilerplate https://github.com/h5bp/html5-boilerplate/blob/v4.2.0/doc/css.md#visuallyhidden
  .mfp-hide {
    border: 0 !important;
    clip: rect(0 0 0 0) !important;
    height: 1px !important;
    margin: -1px !important;
    overflow: hidden !important;
    padding: 0 !important;
    position: absolute !important;
    width: 1px !important;
  }
}

@else {
  .mfp-hide {
    display: none !important;
  }
}

////////////////////////
// 2. Appearance
////////////////////////

// Preloader and text that displays error messages
.mfp-preloader {
  color: $mfp-controls-text-color;
  position: absolute;
  top: 50%;
  width: auto;
  text-align: center;
  margin-top: -0.8em;
  left: 8px;
  right: 8px;
  z-index: $mfp-z-index-base + 4;

  a {
    color: $mfp-controls-text-color;

    &:hover {
      color: $mfp-controls-text-color-hover;
    }
  }
}

// Hide preloader when content successfully loaded
.mfp-s-ready {
  .mfp-preloader {
    display: none;
  }
}

// Hide content when it was not loaded
.mfp-s-error {
  .mfp-content {
    display: none;
  }
}

// CSS-reset for buttons
button {
  &.mfp-close,
  &.mfp-arrow {
    cursor: pointer;
    border: 0;
    -webkit-appearance: none;
    display: block;
    outline: none;
    padding: 0;
    z-index: $mfp-z-index-base + 6;
    box-shadow: none;
    touch-action: manipulation;
  }

  &::-moz-focus-inner {
    padding: 0;
    border: 0;
  }

  &::after,
  &::before {
    display: none;
  }
}

// Close icon
.mfp-close {
  width: 100%;
  min-width: rem(50);
  height: rem(50);
  position: fixed;
  right: 0;
  top: 0;
  text-decoration: none;
  text-align: center;
  opacity: $mfp-controls-opacity;
  padding: 0 0 $space 0;
  background: transparent url('data:image/svg+xml;utf8,%3Csvg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 21.92 21.92"%3E%3Ctitle%3Eicon--close%3C/title%3E%3Cpolygon points="11.67 10.96 21.92 0.71 21.21 0 10.96 10.25 0.71 0 0 0.71 10.25 10.96 0 21.21 0.71 21.92 10.96 11.67 21.21 21.92 21.92 21.21 11.67 10.96" fill="%23fff"/%3E%3C/svg%3E') top right $space-half no-repeat;
  background-size: rem(30);
  text-indent: 9999px;
  margin-top: $space-half;

  &:hover,
  &:focus {
    opacity: 1;
    background-color: transparent;
  }

  @include media('>medium') {
    position: absolute;
    height: rem(30);
    margin-top: rem(15);
  }
}

// "1 of X" counter
.mfp-counter {
  position: absolute;
  top: 0;
  right: 0;
  color: $mfp-controls-text-color;
  font-size: rem(14);
  line-height: rem(18);
  white-space: nowrap;
  font-family: $font-primary;
  font-weight: bold;
}

// Navigation arrows
@if $mfp-include-arrows {
  .mfp-arrow {
    opacity: $mfp-controls-opacity;
    padding: $pad-half;
    width: rem(70);
    height: 70%;
    display: block;
    position: absolute;
    cursor: pointer;
    top: 50%;
    transform: translateY(-50%);

    &:hover,
    &:focus {
      opacity: 1;
      background-color: transparent;
    }
  }

  .mfp-arrow-left {
    left: 0;
    background: transparent url('data:image/svg+xml;utf8,%3Csvg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 30 30"%3E%3Ctitle%3Eicon--arrow-carousel--left%3C/title%3E%3Cpolygon points="20.16 0 20.85 0.51 10.22 15 20.86 29.49 20.16 30 9.14 15 20.16 0" fill="%23fff"/%3E%3C/svg%3E') center center no-repeat;
    background-size: auto rem(50);
  }

  .mfp-arrow-right {
    right: 0;
    background: transparent url('data:image/svg+xml;utf8,%3Csvg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 30 30"%3E%3Ctitle%3Eicon--arrow-carousel--right%3C/title%3E%3Cpolygon points="9.85 30 9.15 29.49 19.78 15 9.14 0.51 9.85 0 20.86 15 9.85 30" fill="%23fff"/%3E%3C/svg%3E') center center no-repeat;
    background-size: auto rem(50);
  }
}

// Image content type
@if $mfp-include-image-type {
  /* Main image in popup */
  img {
    &.mfp-img {
      width: auto;
      max-width: 100%;
      height: auto;
      display: block;
      line-height: 0;
      box-sizing: border-box;
      padding: $mfp-image-padding-top 0 $mfp-image-padding-bottom;
      margin: 0 auto;
    }
  }

  /* The shadow behind the image */
  .mfp-figure {
    line-height: 0;

    &::after {
      content: '';
      position: absolute;
      left: 0;
      top: $mfp-image-padding-top;
      bottom: $mfp-image-padding-bottom;
      display: block;
      right: 0;
      width: auto;
      height: auto;
      z-index: -1;
      box-shadow: $mfp-shadow;
      background: $mfp-image-background;
    }

    small {
      color: $mfp-caption-subtitle-color;
      display: block;
      font-size: 12px;
      line-height: 14px;
    }

    figure {
      margin: 0;
    }
  }

  .mfp-bottom-bar {
    margin-top: -$mfp-image-padding-bottom + 4;
    position: absolute;
    top: 100%;
    left: 0;
    width: 100%;
    cursor: auto;
  }

  .mfp-title {
    text-align: left;
    line-height: 18px;
    color: $mfp-caption-title-color;
    word-wrap: break-word;
    padding-right: 36px; // leave some space for counter at right side
  }

  .mfp-image-holder {
    .mfp-content {
      max-width: 100%;
    }
  }

  .mfp-gallery {
    .mfp-image-holder {
      .mfp-figure {
        cursor: pointer;
      }
    }
  }
}
","/* ------------------------------------*\
    $ARTICLE
\*------------------------------------ */

.c-article__content {
  display: flex;
  flex-direction: column-reverse;
  flex-wrap: nowrap;

  @include media('>small') {
    flex-direction: row;

    &--left {
      width: rem(60);
      flex: auto;
      margin-right: $pad*2;
    }

    &--right {
      width: calc(100% - 100px);
    }
  }
}

.c-article__share {
  display: flex;
  flex-direction: row;
  justify-content: flex-start;
  align-items: center;
  text-align: center;
  margin-top: $space*2;
  z-index: 1;

  @include media('>small') {
    margin-top: 0;
    flex-direction: column;
    justify-content: center;
  }

  &-link {
    margin-left: $space/2;

    @include media('>small') {
      margin-left: 0;
      margin-top: $space/2;
    }
  }
}

.c-article__nav {
  display: flex;
  flex-direction: row;
  flex-wrap: nowrap;
  justify-content: space-between;
  border-top: 1px solid $gray;
  padding-top: $pad;

  &--inner {
    width: 50%;

    &:first-child {
      padding-right: $pad/2;
    }

    &:last-child {
      padding-left: $pad/2;
    }
  }
}

.c-article-product {
  .c-article__body {
    display: flex;
    flex-direction: column;

    @include media('>medium') {
      flex-direction: row;
    }

    .c-article--left {
      @include media('>medium') {
        width: 40%;
        padding-right: $pad;
      }
    }

    .c-article--right {
      @include media('>medium') {
        width: 60%;
        padding-left: $pad;
      }
    }
  }

  .c-article__footer {
    display: flex;
    flex-direction: column;
    align-items: center;

    @include media('>small') {
      flex-direction: row;
      justify-content: space-between;
    }

    &--left {
      display: flex;
      flex-direction: row;
      align-items: center;
      justify-content: center;

      > * {
        @include media('>small') {
          margin: 0 $space 0 0;
        }
      }
    }

    &--right {
      @include media('<=small') {
        margin-top: $space;
      }

      .c-article__share {
        margin: 0;
        flex-direction: row;
        align-items: center;
        justify-content: center;

        > * {
          margin-top: 0;

          @include media('>small') {
            margin-left: $space/2;
          }
        }
      }
