}

// Article Body list styles from u-font--styles.scss
ol,
ul {
  .c-article__body & {
    margin-left: 0;
    margin-top: 0;

    li {
      list-style: none;
      padding-left: $pad;
      text-indent: rem(-10);

      &::before {
        color: $primary-color;
        width: rem(10);
        display: inline-block;
        font-size: rem(30);
      }

      li {
        list-style: none;
      }
    }
  }
}

ol {
  .c-article__body & {
    counter-reset: item;

    li {
      &::before {
        content: counter(item) ". ";
        counter-increment: item;
        font-size: 90%;
      }

      li {
        counter-reset: item;

        &::before {
          content: "\002010";
        }
      }
    }
  }
}

ul {
  .c-article__body & {
    li {
      &::before {
        content: "\002022";
      }

      li {
        &::before {
          content: "\0025E6";
        }
      }
    }
  }
}

.c-article {
  margin-left: auto;
  margin-right: auto;
  padding-top: $pad*2;
  padding-bottom: $pad*4;
}

.c-article__body {
  &__image {
    outline: 0;
  }

  > *,
  figcaption,
  ul {
    max-width: rem(700);
    margin: 0 auto;
  }

  > .c-article--left {
    max-width: 100%;
    margin-bottom: $space;
  }

  &.has-dropcap > p:first-child::first-letter {
    color: $secondary-color;
    float: left;
    font-size: rem(60);
    margin-top: rem(15);
    margin-right: rem(10);
  }

  a {
    text-decoration: underline;
  }

  .o-button {
    text-decoration: none;
  }

  p,
  ul,
  ol,
  dt,
  dd {
    @include p;
  }

  p span,
  p strong span {
    font-family: $font !important;
  }

  strong {
    font-weight: bold;
  }

  > p:empty,
  > h2:empty,
  > h3:empty {
    display: none;
  }

  > h1,
  > h2,
  > h3,
  > h4,
  > h5 {
    margin-top: $space*2;

    &:first-child {
      margin-top: 0;
    }
  }

  > h1 {
    @include u-font--primary--l;
  }

  > h2 {
    @include u-font--primary--m;
  }

  > h3 {
    @include u-font--xl;
  }

  h4,
  h5 {
    @include u-font--secondary--s;

    color: $secondary-color;
    margin-bottom: rem(-30);
  }

  h1 + ul,
  h2 + ul,
  h3 + ul,
  h4 + ul,
  h5 + ul {
    display: block;
    margin-top: rem(30);
  }

  img {
    height: auto;
  }

  hr {
    margin-top: rem(15);
    margin-bottom: rem(15);

