      margin-top: rem(30);
      margin-bottom: rem(30);
    }
  }

  figcaption {
    @include u-font--s;
  }

  blockquote {
    p {
      @include u-font--xl;

      color: $secondary-color;
      font-style: italic;
    }

    padding-left: $pad;
    border-left: 1px solid $gray;

    @include media('>large') {
      padding-left: $pad*2;
    }
  }

  .wp-caption-text {
    display: block;
    line-height: 1.3;
    text-align: left;
    margin-top: rem(5);
  }

  .aligncenter {
    margin-left: auto;
    margin-right: auto;
    text-align: center;

    figcaption {
      text-align: center;
    }
  }

  .alignleft,
  .alignright {
    min-width: 50%;
    max-width: 50%;

    img {
      width: 100%;
    }
  }

  .alignleft {
    float: left;
    margin: 0 $space-and-half $space 0;
  }

  .alignright {
    float: right;
    margin: 0 0 $space $space-and-half;

    @include media('>large') {
      margin-right: rem(-100);
    }
  }

  .size-thumbnail {
    max-width: rem(400);
    height: auto;
  }
}

.c-article--right {
  .alignleft,
  .alignright {
    min-width: 33.33%;
    max-width: 33.33%;

    img {
      width: 100%;
    }
  }

  .alignright {
    @include media('>large') {
      margin-right: 0;
    }
  }
}
