}

/**
 * Path Fills
 */
.u-path-fill--white {
  path {
    fill: $white;
  }
}

.u-path-fill--black {
  path {
    fill: $black;
  }
}

.u-fill--white {
  fill: $white;
}

.u-fill--black {
  fill: $black;
}
","/* ------------------------------------*\
    $DISPLAY STATES
\*------------------------------------ */

/**
 * Completely remove from the flow and screen readers.
 */
.is-hidden {
  display: none !important;
  visibility: hidden !important;
}

.u-hide {
  display: none;
}

/**
 * Completely remove from the flow but leave available to screen readers.
 */
.is-vishidden,
.screen-reader-text,
.sr-only {
  position: absolute !important;
  overflow: hidden;
  width: 1px;
  height: 1px;
  padding: 0;
  border: 0;
  clip: rect(1px, 1px, 1px, 1px);
}

.has-overlay {
  background: linear-gradient(rgba($black, 0.45));
}

/**
 * Display Classes
 */
.u-display--inline-block {
  display: inline-block;
}

.u-display--flex {
  display: flex;
}

.u-display--table {
  display: table;
}

.u-display--block {
  display: block;
}

.u-hide-until--s {
  @include media ('<=small') {
    display: none;
  }
}

.u-hide-until--m {
  @include media ('<=medium') {
    display: none;
  }
}

.u-hide-until--l {
  @include media ('<=large') {
    display: none;
  }
}

.u-hide-until--xl {
  @include media ('<=xlarge') {
    display: none;
  }
}

.u-hide-until--xxl {
  @include media ('<=xxlarge') {
    display: none;
  }
}

.u-hide-until--xxxl {
  @include media ('<=xxxlarge') {
    display: none;
  }
}

.u-hide-after--s {
  @include media ('>small') {
    display: none;
  }
}

.u-hide-after--m {
  @include media ('>medium') {
    display: none;
  }
}

.u-hide-after--l {
  @include media ('>large') {
    display: none;
  }
}

.u-hide-after--xl {
  @include media ('>xlarge') {
    display: none;
  }
}

.u-hide-after--xxl {
  @include media ('>xxlarge') {
    display: none;
  }
}

.u-hide-after--xxxl {
  @include media ('>xxxlarge') {
    display: none;
  }
}
","/* ------------------------------------*\
    $FILTER STYLES
\*------------------------------------ */
","/* ------------------------------------*\
    $SPACING
\*------------------------------------ */

/**
 * Padding
 */

.u-padding {
  padding: $pad;

  &--top {
    padding-top: $pad;
  }

  &--bottom {
    padding-bottom: $pad;
  }

  &--left {
    padding-left: $pad;
  }

  &--right {
    padding-right: $pad;
  }

  &--quarter {
    padding: $pad/4;

    &--top {
      padding-top: $pad/4;
    }

    &--bottom {
      padding-bottom: $pad/4;
    }
  }

  &--half {
    padding: $pad/2;

    &--top {
      padding-top: $pad/2;
    }

    &--bottom {
      padding-bottom: $pad/2;
    }
  }

  &--and-half {
    padding: $pad*1.5;

    &--top {
      padding-top: $pad*1.5;
    }

    &--bottom {
      padding-bottom: $pad*1.5;
    }
  }

  &--double {
    padding: $pad*2;

    &--top {
      padding-top: $pad*2;
    }

    &--bottom {
      padding-bottom: $pad*2;
    }
  }

  &--triple {
    padding: $pad*3;
  }

  &--quad {
    padding: $pad*4;
  }

  &--zero {
    padding: 0;

    &--top {
      padding-top: 0;
    }

    &--bottom {
      padding-bottom: 0;
    }
  }
}

/**
 * Space
 */

.u-space {
  margin: $space;

  &--top {
    margin-top: $space;
  }

  &--bottom {
    margin-bottom: $space;
  }

  &--left {
    margin-left: $space;
  }

  &--right {
    margin-right: $space;
  }

  &--quarter {
    margin: $space/4;

    &--top {
      margin-top: $space/4;
    }

    &--bottom {
      margin-bottom: $space/4;
    }

    &--left {
      margin-left: $space/4;
    }

    &--right {
      margin-right: $space/4;
    }
  }

  &--half {
    margin: $space/2;

    &--top {
      margin-top: $space/2;
    }

    &--bottom {
      margin-bottom: $space/2;
    }

    &--left {
      margin-left: $space/2;
    }

    &--right {
      margin-right: $space/2;
    }
  }

  &--and-half {
    margin: $space*1.5;

    &--top {
      margin-top: $space*1.5;
    }

    &--bottom {
      margin-bottom: $space*1.5;
    }
  }

  &--double {
